import styled from "styled-components";

export const Container = styled.div`
  width: 100%;
  height: 48px;
  background: var(--color-footer);
  border-top: 1px solid var(--color-border);

  display: flex;
  align-items: center;
  justify-content: center;

  margin-top: 30px;
`;
